//Helper function.
void Swap(double* a, double* b)
{
    if(*a > *b)
    {
        double c = *a;
        *a = *b;
        *b = c;
    }
}

//Helper function 2.
double* DouCpy(double const* src, size_t len)
{
    double* p = malloc(len * sizeof(double));
    memcpy(p, src, len*sizeof(double));
    return p;
}

int main(void)
{
    //M,L,m,l,g
    double param[5] = {1.0, 1.0, 1.7, 1.01, 1.0};
    
    gsl_odeiv2_system system = {Func, NULL, 4, param};
    gsl_odeiv2_driver* driver = 
            gsl_odeiv2_driver_alloc_y_new(&system, gsl_odeiv2_step_rk8pd, 1e-8, 1e-12, 1e-12);
    double t = 0.0, ti = 0.0;
    
    //Time-step increments for RK-45.
    const double t_step = 0.01;
    
    //Initial conditions array.
    double y[4] = {0.0, 0.0, 0.0, 0.0};
    
    //Previous data array.
    double prev_y[4] = {0.0, 0.0, 0.0, 0.0};
    
    for(int j = 1; j <= 10; ++j)
    {
        t=0;
        y[0]=0.0;
        y[1]=0.0;
        y[2]=0.01;
        y[3]=0.02 + 0.095*j;
        
        //Solve the equations.
        for(int i = 1; i <= 10000; ++i)
        {
            for(int k = 0; k < 4; ++k) prev_y[k] = y[k]; //Save numeric data.
            
            ti = i*t_step; //Current time.
            int status = gsl_odeiv2_driver_apply(driver, &t, ti, y);
            if(status != GSL_SUCCESS)
            {
                fprintf(stderr, "Error with return: %d\n", status);
                break;
            }
            y[0] = fmod(y[0], 2.0*M_PI);
            y[1] = fmod(y[1], 2.0*M_PI);
            
            //Derivative to use with the secant method.
            double dy0dt = (y[0] - prev_y[0])/t_step;
            
            //Plotting poincare section for theta = 0.
            if((y[0]-prev_y[0]) < 0.0)
            {
                //Apply Newton's method to find the exact t value.
                double t_new = t - y[0]/dy0dt;
                
                
                
                double* x = DouCpy(y, sizeof(y)/sizeof(double)); //Copy the current values so we can do stuff on them.
                double T = 0.0; //Copy the current time.
                double TI = 0.0; //^^^
                
                for(int k = 1; k <= 500; ++k)
                {
                    TI = k*t_step;
                    status = gsl_odeiv2_driver_apply(driver, &T, TI, x);
                    if(status != GSL_SUCCESS)
                    {
                        fprintf(stderr, "Error with return: %d\n", status);
                        break;  
                    }
                    
                    x[1] = fmod(x[1], 2.0*M_PI);
                    
                    printf("%.5e %.5e\n", x[1], x[3]);
                }
            }
        }
    }
    
    //printf("End\n");
    
    gsl_odeiv2_driver_free(driver);
    return 0;
}
